import { createSlice } from '@reduxjs/toolkit'

// 1. How do we wanna do the initialState. localStorage or like in Maks video?
// const initialState = localStorage

// 2. Created all the reducers based on the useStates in PostFormLogic.js

const form = createSlice({
  name: 'form',
  initialState: {
    petposts: []
  },
  reducers: {
    setPetPosts: (store, action) => {
      store.petposts = action.payload
    },
    setAddSinglePost: (store, action) => {
      store.petposts = [...store.petposts, action.payload]
    },
  }
})

export default form
