import styled from 'styled-components/macro'

import elaine from '../../assets/elaineandalfie.jpg'
import malin from '../../assets/malinandfrasse2.jpg'
import logo from '../../assets/linkedin_testicon.png'


const AboutContainer = styled.main`
  margin: 0;
  width: 100%;
  height: 100%;
`

const ArticleContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 40px;

  @media (min-width: 768px) {
    flex-direction: row;
    margin-left: 30px;
    flex-wrap: wrap;
  }
`

const Wrapper = styled.div`

`

const DescriptionConatainer = styled.div`
display: flex;
justify-content: center;
`

const Title = styled.h1`
  color: black;
  border-bottom: 2px solid black;
  margin: 10px;
`

const Description = styled.p`
  font-size: 20px;
  margin: 20px;
  max-width: 80%;
`

const Icon = styled.img`
  width: 20px;
`

const ArticleElaine = styled.article`
color: #384E52;
width: 50%;
text-align: left;
margin-bottom: 50px;
padding: 0 20px 20px 20px;
max-width:40%;
`

const ArticleMalin = styled.article`
color: #384E52;
width: 50%;
text-align: left;
margin-bottom: 50px;
padding: 0 20px 20px 20px;
max-width:40%;

@media (min-width: 768px) {
  display: flex;
  flex-direction: row-reverse;
}
`

const ArticleTitle = styled.h2`
margin-top: 0;

`

const ArticleText = styled.p`

`

const ImageContainer = styled.div`
width: 50%;
display: flex;
justify-content: center;
margin-bottom: 50px;
`

const Image = styled.img`
  margin: 0;
  width: 300px;
  height: 300px; 
  object-fit: cover;
  border-radius: 50%;
  box-shadow: #50B9CD 8px 8px 8px;
`

const About = () => {
  return (
    <>
    <AboutContainer>
      <Title>About</Title>
      <DescriptionConatainer>
        <Description>Petspotter is the result of a 24 weeks frontend dev bootcamp. We wanted to make a project that was close to our hearts.  </Description>
      </DescriptionConatainer>
      <ArticleContainer>
        <Wrapper>
          <ArticleElaine>
            <ArticleTitle>Elaine Bergström</ArticleTitle>
