import React from 'react'
import styled from 'styled-components/macro'

import highFive from '../assetsMG/highfive.jpg'


const ArticleContainer = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column-reverse;
  max-height: 600px;
  padding: 16px;

  img {
    width: 50%;
    object-fit: contain;    
  }
`

const Text = styled.p`
  color: #384E52;
  font-size: 40px;
`

const About = () => {
  return (
    <ArticleContainer>
      <img className="kitten-high-five" src={highFive} alt="cat and owner hugging" />
      <Text>
        Our goal is to help reunite the  lost pets of 
        the world to their rightful families. 
        Help us help you!
        <a href="description">Pet Postings</a>
      </Text>
    </ArticleContainer>
  )
}

export default About
