import { GrLocation } from 'react-icons/gr'
import { IconContext } from "react-icons";
import styled from 'styled-components'

import { CardWrapper, Card, InnerCard, ImageThumbnail, Space, StatusTitle, TextWrapper, Text, ImportantText, LocationText } from '../styled-components/CardElements'
import Logo from '../Logo';

const CardList = ({ cards }) => {
  return (
      <CardWrapper>
        {cards.map((card) => (
            <Card key={card.petCard._id}>
              <InnerCard>
                <ImageThumbnail>
                  <Logo />
                </ImageThumbnail>
                <StatusTitle>
                  { card.petCard.status }
                </StatusTitle>
                <TextWrapper>
                  <Text>
                    "{ card.petCard.petName }"
                  </Text>
                  <ImportantText>
                    { card.petCard.breed }
                   <Space> { card.petCard.species } </Space>
                  </ImportantText>
                  <div>
                    <LocationText>
                    <IconContext.Provider value={{ color: 'blue', size: '20px' }}>
                      <div>
                        < GrLocation />
                      </div>                   
                    </ IconContext.Provider>
                    { card.petCard.location }
                    </LocationText>
                  </div>
                </TextWrapper>
              </InnerCard>
            </Card>
        ))}     
      </CardWrapper> 
  )
}

// const GrLocation  = styled(GrLocation)`
//   color: skyblue;
// `;

export default CardList;
