import styled from "styled-components/macro";

export const MainContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  font-family: "Nunito", sans-serif;
  box-sizing: content-box;
  margin-top: 5rem;
`;

export const Header = styled.h1`
  font-weight: 300;
  font-size: 35px;
  margin-top: 15px;
  @media (min-width: 768px) {
    font-size: 40px;
  }
`;

export const TextArea = styled.textarea`
  border-radius: 10px;
  border: 3px dotted #50b9cd;
  box-sizing: border-box;
  background-color: white;
  padding: 15px;
  width: 100%;
  height: 200px;
`;

export const InputField = styled.input`
  border-radius: 0 10px 10px 10px;
  width: 100%;
  margin: 15px 0;
  box-sizing: border-box;
  border: 2px solid #50b9cd;
  background-color: white;
  padding: 15px;
`;

export const Form = styled.form`
  width: 80%;
  height: fit-content;
  display: flex;
  flex-direction: column;
  border: 3px solid white;
  border-radius: 20px;
  align-items: flex-start;
  margin: 30px auto;
  padding: 10px;
  background-color: #54b0c1;
  @media (min-width: 768px) {
    max-width: 650px;
    margin: 30px auto;
  }
`;
export const InputWrapper = styled.div`
  text-align: left;
  margin: 0 auto;
  height: auto;
  width: 100%;
  padding: 16px;
  box-sizing: border-box;
`;

export const TitleLabel = styled.label`
  font-weight: bold;
  padding: 10px 10px 0 0;
  font-size: 18px;
  color: white;
  border-bottom: 2px solid white;
  margin-bottom: 16px;
`;

export const Label = styled.label`
  font-weight: bold;
  padding: 10px 10px 10px 0;
  color: white;
`;


export const Text = styled.p`
  font-weight: 300;
  margin-top: 15px;
`;

export const ButtonContainer = styled.div`
  width: 100%;
  margin-bottom: 40px;
  text-align: center;
  display: flex;
  align-items: center;
  flex-direction: column;
`;

export const PrimaryButton = styled.button`
  border-radius: 10px;
  font-size: 20px;
  font-weight: 200;
  width: 8rem;
  margin: 10px 0 10px 0;
  padding: 10px 20px;
  border: 3px solid white;
  background-color: #8fe0ef;
  &:hover {
    background-color: #d1f8ff;
  }
  cursor: pointer;
`;

export const SecondaryButton = styled.button`
  font-family: "Nunito", sans-serif;
  border-radius: 10px;
  font-size: 20px;
  font-weight: 200;
  width: 8rem;
  margin-bottom: 30px;
  padding: 10px 20px;
  border: 3px solid white;
  background-color: #8fe0ef;
  &:hover {
    background-color: #d1f8ff;
  }
  cursor: pointer;
`;

export const ErrorMessage = styled.div`
  border-radius: 20px;
  font-size: 16px;
  font-weight: 300;
  width: fit-content;
  padding: 10px;
  background-color: #FFB560;
`;

export const ButtonWrapper = styled.div`
  height: auto;
  width: 100%;
  padding: 16px
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  align-items: center;
`

export const Wrapper = styled.div`
  height: auto;
  width: 100%;
  padding: 16px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
`;

export const RadioWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
`;

export const RadioDiv = styled.div`
  margin-left: 3rem;
  display: flex;
  flex-direction: row;
  align-items: center;
`;
// styled radio? stretch goal?
export const RadioButton = styled.input`
  border-radius: 50%;
  width: 24px;
  height: 24px;
  margin-right: 10px;
`;

export const SelectField = styled.select`
  text-align: left;
  margin: 20px auto;
  width: 100%;
  height: 3rem;
  border-radius: 0 10px 10px 10px;
  border: none;
  background-color: white;
  font-family: "Nunito", sans-serif;
`;
