import React from 'react';
import { useDispatch, useSelector, batch} from 'react-redux'
import { Link } from 'react-router-dom'

import user from '../reducers/user'
import { NavLink, NavMenu, ButtonWrapper } from './styled-components/NavbarElements';
import { PrimaryButton } from './styled-components/FormElements'
import CreatePostButton from './CreatePostButton';


const Navbar = () => {
  const dispatch = useDispatch()
  const accessToken = useSelector(store => store.user.accessToken)

  const onButtonClick = () => {
    batch(() => {
      // remove user from localStorage
      localStorage.removeItem('user')
      dispatch(user.actions.setUsername(null));
      dispatch(user.actions.setAccessToken(null))
    })
  }

  if (accessToken) {
    return (  
      <>
      <Navbar>
        <NavMenu>
        <NavLink to='/'>
          Home
        </NavLink>
        <NavLink to='/petposts'>
          Posts
        </NavLink>
        <NavLink to='/about-us'>
          About Us
        </NavLink>
        <NavLink to='/contact'>
          Contact
        </NavLink>
        </NavMenu>
        <ButtonWrapper>
          <CreatePostButton />
          <PrimaryButton onClick={onButtonClick} > Logout </PrimaryButton>
        </ButtonWrapper>
      </Navbar>
      </>
    )
  } else { 
    return (  
	<>
	<Navbar>
		<NavMenu>
		<NavLink to='/' >
			Home
		</NavLink>
		<NavLink to='/petposts'>
			Pet Posts
		</NavLink>
		<NavLink to='/about-us'>
			About Us
		</NavLink>
		<NavLink to='/contact'>
			Contact
		</NavLink>
		</NavMenu>
    <ButtonWrapper>
          <Link to="/login">
            <PrimaryButton>
              Login
            </PrimaryButton>
          </Link>
    </ButtonWrapper>     
	</Navbar>
	</>
)};
};

export default Navbar;
