import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom';
import styled from 'styled-components/macro';


const PetPostDetails = () => {
  const { id } = useParams();
  const [details, setDetails] = useState({});

  const PET_DETAILS_URL = `http://localhost:8080/posts/${id}`

  useEffect(() => {
    fetch(PET_DETAILS_URL)
      .then((res) => res.json())
      .then((res) => setDetails(res));
  }, [PET_DETAILS_URL]);

  return (
    <CardWrapper className="main-container">
     <Card>
       <InnerCard>
         <ImageWrapper>
           Image Here
          {/* <img src={details.imageUrl} alt="pet"/> */}
         </ImageWrapper>
         <InfoContainer>
           info
         </InfoContainer>
       </InnerCard>
     </Card>
    </CardWrapper>
  )
}

export default PetPostDetails

const CardWrapper = styled.main`
  margin: 0;
  padding: 20px;
`;
const Card = styled.article`
  width: 100%;
  min-height: 300px;
  margin: 30px;
  border-radius: 0 10px 10px 10px;
  padding: 5px 10px 10px 5px;
  background-color: #50b9cd;
  box-sizing: border-box;
  box-shadow: 0 8px 6px -6px grey;
`;

const InnerCard = styled.div`
  border-radius: 0 10px 10px 10px;
  display: flex;
  align-items: center;
  flex-direction: row;
  min-height: 270px
  width: 100%;
  background-color: white;
`;

const InfoContainer = styled.section`
  width: 50%;
  height: 100%;
  background-color: grey;
`;

const StatusTitle = styled.h1`
  border-radius: 0 10px 10px 10px;
  background-color: #ffb560;
  color: white;
  padding: 1px 8px;
  font-size: 18px;
  margin: 5px 0;
  position: relative;
  left: -90px;
  height: 35px;
  width: 55px;
  text-align: center;
`;

const ImageWrapper = styled.div `
  width: 50%
  height: 100%;
  background-color: pink;
`;

const Image = styled.img`
  object-fit: cover;
`;
