import React, { useState, useDispatch } from 'react';

import PostElements from './PostElements';

const PostForm = () => {
  const [status, setStatus] = useState('')
  const [petName, setPetName] = useState('')
  const [species, setSpecies] = useState('')
  const [breed, setBreed] = useState('')
  const [sex, setSex] = useState('')
  const [location, setLocation] = useState('')
  const [description, setDescription] = useState('')
  const [email, setEmail] = useState('')

  return (
    <div className="create-post">
      <h2>Create a new post!</h2>
      <form>
        <label>Pet Status:</label>
        <input 
          type="radio"
          required
          value={status}
          onChange={(e) => setStatus(e.target.value)}
        />
        <label>Lost</label>
        <input 
          type="radio"
          required
          value={status}
          onChange={(e) => setStatus(e.target.value)}
        />
        <label>Found</label>
        <input 
          type="radio"
          required
          value={status}
          onChange={(e) => setStatus(e.target.value)}
        />
        <label>Returned Home</label>
        <label>Pet Name:</label>
        <input 
          type="text"
          value={petName}
          onChange={(e) => setPetName(e.target.value)}
        />
        <label>Species:</label>
        <select 
          value={species}
          onChange={(e) => setSpecies(e.target.value)}
        >
          <option 
          value="Dog">Dog</option>
          <option value="Cat">Cat</option>
        </select>
        <label>Breed:</label>
        <input
          type="text"
          required
          value={breed}
          onChange={(e) => setBreed(e.target.value)}
        />
        <label>Sex:</label>
        <input 
          type="radio"
          required
          value={sex}
          onChange={(e) => setSex(e.target.value)}
        />
        <label>Female</label>
        <input 
          type="radio"
          required
          value={sex}
          onChange={(e) => setSex(e.target.value)}
        />
        <label>Male</label>
        <input 
          type="radio"
          required
          value={sex}
          onChange={(e) => setSex(e.target.value)}
        />
        <label>Other</label>
        <label>Location:</label>
        <input 
          type="text"
          required
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
        <label>Description:</label>
        <textarea
          placeholder="Please describe in as much detail as possible.
           Fur color/patterns, eye color, distinguishing marks etc."
          required
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        >
        </textarea>
        <label>Contact Email:</label>
        <input 
          type="email"
          required
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <button>Add Post</button>
        <button>Cancel</button>
      </form>
    </div>
  )
}

export default PostForm;
