import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom';
import styled from 'styled-components';


const PetPostDetails = () => {
  const { id } = useParams();
  const [details, setDetails] = useState([{}]);

  const PET_DETAILS_URL = `http://localhost:8080/posts/${id}`

  useEffect(() => {
    fetch(PET_DETAILS_URL)
      .then((res) => res.json())
      .then((data) => setDetails(data));
  }, [PET_DETAILS_URL]);

  return (
    <CardWrapper className="main-container">
     <Card>
       <InnerCard>
         <ImageWrapper>
          <img src={details.imageUrl} alt="pet"/>
         </ImageWrapper>
       </InnerCard>
     </Card>
    </CardWrapper>
  )
}

export default PetPostDetails

const CardWrapper = styled.main`
  margin: 0;
  padding: 20px;
  display: flex;
  flex-direction: row;
`;
const Card = styled.article`
  width: 100%;
  min-height: 300px;
  margin: 30px;
  border-radius: 0 10px 10px 10px;
  padding: 5px 10px 10px 5px;
  background-color: #50b9cd;
  box-sizing: border-box;
  box-shadow: 0 8px 6px -6px grey;
`;

const InnerCard = styled.div`
  border-radius: 0 10px 10px 10px;
  display: flex;
  align-items: center;
  flex-direction: column;
  height: 100%;
  width: 100%;
  background-color: white;
`;

const StatusTitle = styled.h1`
  border-radius: 0 10px 10px 10px;
  background-color: #ffb560;
  color: white;
  padding: 1px 8px;
  font-size: 18px;
  margin: 5px 0;
  position: relative;
  left: -90px;
  height: 35px;
  width: 55px;
  text-align: center;
`;

const ImageWrapper = styled.div `
  width: 50%
`

const Image = styled.img`
  object-fit: cover;
`;
