import React from "react";
import { PostLink as Link } from "react-router-dom";
import styled from "styled-components/macro";
import { IoIosArrowForward } from "react-icons/io";

import hugImg from "../assets/article.jpg";

const ArticleContainer = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column-reverse;
  max-height: 600px;
  padding: 16px;
`;

const Image = styled.img`
  height: 200px;
`
const Title = styled.h2`
  color: #FFB560;
`
const PostLink = styled(Link)`
  text-decoration: none;
`

const Text = styled.p`
  color: #384e52;
  font-size: 20px;
`;

const ArticleSection = () => {
  return (
    <>
      <ArticleContainer>
        <Image alt="kittenhug" className="article-img" src={hugImg} />
        <Text>
          Our goal is to help reunite the lost pets of the world to their
          rightful families. Help us help you!
        </Text>
        <PostLink to="/petposts">
          <Title>Pet Postings <IoIosArrowForward /><IoIosArrowForward /> </Title>
        </PostLink>
      </ArticleContainer>
    </>
  );
};

export default ArticleSection;
