const dispatch = useDispatch

const onButtonClick = () => {
  batch(() => {
    // remove user from localStorage
    localStorage.removeItem('user')
    dispatch(user.actions.setUsername(null));
    dispatch(user.actions.setAccessToken(null))
  })
}

const Navbar = () => {

return (
	<>
	<Nav>
		<Bars />
		<NavMenu>
		<NavLink to='/home' activeStyle>
			Home
